import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';

const UpdateEmployeeComponent = () => {
    const [state, setState] = useState({
        id: '',
        firstName: '',
        lastName: '',
        emailId: ''
    });
    const navigate = useNavigate();

    useEffect(() => {
        const employeeId = window.location.pathname.split('/').pop();
        EmployeeService.getEmployeeById(employeeId).then((res) => {
            const employee = res.data;
            setState({
                firstName: employee.firstName,
                lastName: employee.lastName,
                emailId: employee.emailId
            });
        });
    }, []);

    const updateEmployee = (e) => {
        e.preventDefault();
        const updatedEmployee = {
            id: state.id,
            firstName: state.firstName,
            lastName: state.lastName,
            emailId: state.emailId
        };
        EmployeeService.updateEmployee(updatedEmployee).then((res) => {
            navigate('/employees');
        });
    };

    const cancel = () => {
        navigate('/employees');
    };

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setState({ ...state, [name]: value });
    };

    return (
        <div>
            <br />
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        <h3 className="text-center">Update Employee</h3>
                        <div className="card-body">
                            <form>
                                <div className="form-group">
                                    <label>First Name:</label>
                                    <input
                                        type="text"
                                        name="firstName"
                                        className="form-control"
                                        value={state.firstName}
                                        onChange={handleInputChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Last Name:</label>
                                    <input
                                        type="text"
                                        name="lastName"
                                        className="form-control"
                                        value={state.lastName}
                                        onChange={handleInputChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Email Id:</label>
                                    <input
                                        type="email"
                                        name="emailId"
                                        className="form-control"
                                        value={state.emailId}
                                        onChange={handleInputChange}
                                    />
                                </div>

                                <button className="btn btn-success" onClick={updateEmployee}>
                                    Save
                                </button>
                                <button className="btn btn-danger" onClick={cancel} style={{ marginLeft: '10px' }}>
                                    Cancel
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UpdateEmployeeComponent;
