import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CourseService from "../../services/CourseService";
import DepartmentService from "../../services/DepartmentService";
import EmployeeService from "../../services/EmployeeService";

const CourseForm = () => {
  const courseId = window.location.pathname.split("/").pop();
  console.log("awterfvaweudfyuWE", courseId);
  const [course, setCourse] = useState({
    id: "",
    courseName: "",
    departmentId: "",
    employeeId: "",
  });

  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);

  const navigate = useNavigate();
  useEffect(() => {
    console.log("Pichaneku", courseId);

    // Fetch departments only when the component mounts
    DepartmentService.getAllDepartments().then((res) => {
      setDepartments(res.data || []);
    });

    // Fetch employees (initially for the first department)
    if (departments.length > 0) {
      fetchEmployeesByDepartment(departments[0].id);
    }

    // Fetch course details if it's an update operation
    if (courseId !== "_add") {
      CourseService.getCourseById(courseId)
        .then((res) => {
          const courseDetails = res.data;
          setCourse({
            id: courseDetails.id,
            courseName: courseDetails.courseName,
            departmentId: courseDetails.departmentId,
            employeeId: courseDetails.employeeId,
          });

          // Fetch employees for the selected department
          fetchEmployeesByDepartment(courseDetails.departmentId);
        })
        .catch((error) => {
          console.error("Error fetching course details:", error);
        });
    }
  }, [courseId]); // Dependencies include only courseId

  /*const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCourse({ ...course, [name]: value });
  };
  const handleDepartmentChange = (e) => {
    const departmentId = e.target.value;
    setCourse({ ...course, departmentId });
    fetchEmployeesByDepartment(departmentId);
  };

  */
  const handleInputChange = (e) => {
    const { name, value } = e.target;

    if (name === "departmentId") {
      fetchEmployeesByDepartment(value);
    }

    setCourse((prevCourse) => ({
      ...prevCourse,
      [name]: value,
    }));
  };
  const handleDepartmentChange = (e) => {
    const departmentId = e.target.value;
    setCourse({ ...course, departmentId });

    // Fetch employees for the selected department
    fetchEmployeesByDepartment(departmentId);

    // Set employeeId to the first employee in the list (if available)
    const firstEmployee = employees[0];
    if (firstEmployee) {
      setCourse((prevCourse) => ({
        ...prevCourse,
        employeeId: firstEmployee.id,
      }));
    }
  };

  const fetchEmployeesByDepartment = (departmentId) => {
    EmployeeService.getEmployeesByDepartment(departmentId).then((res) => {
      setEmployees(res.data || []);
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const course1 = {
        courseName: course.courseName,
        departmentId: course.departmentId,
        employeeId: course.employeeId,
      };
      if (courseId !== "_add") {
        const response = CourseService.updateCourse(course1, courseId);
        if (!response.ok) {
          console.error("Course Not added/updated-1");
          return;
        }
        const data = await response.json();
        if (data.success) {
          console.log("Course added/updated successfully");
          navigate(`/courses`);
        } else {
          console.error("Course Not added/updated-2");
        }
      } else {
        console.log("Course added---------");
        const responsetoAdd = CourseService.createCourse(course1);

        console.log("successfully",responsetoAdd);
        if (!responsetoAdd.ok) {
          console.error("Course Not added/updated-1");
          return;
        }
        const data = await responsetoAdd.json();
        if (data.success) {
          console.log("Course added/updated successfully");
          navigate(`/courses`);
        } else {
          console.error("Course Not added/updated-2");
        }
      }
    } catch (error) {
      console.error("Error saving/updating course: ", error);
    }
  };

  return (
    <div>
      <h3 className="text-center">
        {course.id ? "Update Course" : "Add Course"}
      </h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Course Name:</label>
          <input
            type="text"
            name="courseName"
            value={course.courseName}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Department:</label>
          <select onChange={handleDepartmentChange} required>
            <option value="">Select Department</option>
            {departments.map((department) => (
              <option key={department.id} value={department.id}>
                {department.name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Employee:</label>
          <select
            name="employeeId"
            value={course.employeeId}
            onChange={handleInputChange}
            required
          >
            <option value="">Select Employee</option>
            {employees.map((employee) => (
              <option key={employee.id} value={employee.id}>
                {employee.firstName} {employee.lastName}
              </option>
            ))}
          </select>
        </div>
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default CourseForm;
