import React, { useState } from 'react';
import './LoginComponent.css'; 
import './ViewEmployeeComponent.css'; 
import { useNavigate } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';

const LoginComponent = () => {
    const navigate = useNavigate();
    const [state, setState] = useState({
        username: '',
        password: ''
    });

    const handleLogin = async () => {
      try {

        const user = {
            username: state.username,
            password: state.password
        };

        const userDetails = await EmployeeService.getUserDetails();
        if (userDetails) {
            // Check if the entered password matches the user's password in userDetails
            if (password === userDetails.password) {
                // Successful login
                console.log('Login successful');
                navigate('/employees'); // Redirect to the employees page or any desired route
            } else {
                // Incorrect password
                console.log('Incorrect password');
                navigate('/login');
            }
        } else {
            // User not found
            console.log('User not found');
        }
      } catch (error) {
        console.error('Error during login:', error);
      }
    };
    const changeUserNameHandler = (event) => {
        setState({ ...state, username: event.target.value });
    };

    const changePasswordHandler = (event) => {
        setState({ ...state, password: event.target.value });
    };

    return (
        <div className="login-container">
            <div className="view-employee-container"> 
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Login</h3>
                <div className="card-body">
                    <div className="row">
                       <label>Username:</label>
                       <input type="text" value={username} onChange={(e) => setState(e.target.value)} />
                    </div>
                    <div className="row">
                       <label>Password:</label>
                       <input type="password" value={password} onChange={(e) => setState(e.target.value)} />
                    </div>
                    <div className="row">
                    <button type="button" onClick={handleLogin}>Login</button>
                    </div>
                </div>
            </div>
        </div>
        </div>

    );
};

export default LoginComponent;
