import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';

const CreateEmployeeComponent = () => {
    const navigate = useNavigate();
    const [state, setState] = useState({
        id: this.this.props.match.params.id,
        firstName: '',
        lastName: '',
        emailId: ''
    });

    const saveEmployee = (e) => {
        e.preventDefault();
        let employee = { firstName: state.firstName, lastName: state.lastName, emailId: state.emailId };
        console.log('employee => ' + JSON.stringify(employee));
        EmployeeService.createEmployee(employee).then(res =>{
            navigate('/employees');
        }); 
    };

    useEffect(() => {
        const employeeId = window.location.pathname.split('/').pop();

        EmployeeService.getEmployeeById(employeeId).then((res) => {
            const employee = res.data;
            setState({
                id: employee.id,
                firstName: employee.firstName,
                lastName: employee.lastName,
                emailId: employee.emailId
            });
        });
    }, []);

    const updateEmployee = async (e) => {
        e.preventDefault();
        try {
            const updatedEmployee = {
                firstName: state.firstName,
                lastName: state.lastName,
                emailId: state.emailId
            };
            console.log('employee => ', updatedEmployee);
            console.log('id => ', state.id);

            await EmployeeService.updateEmployee(updatedEmployee, state.id);

            // Assuming the update was successful, navigate to the employees page
            navigate('/employees');
        }
         catch (error) {
            console.error('Error updating employee: ', error);
        }
    };









    const cancel = () => {
        // Navigate back to the employees page
        navigate('/employees');
    };

    const changeFirstNameHandler = (event) => {
        setState({ ...state, firstName: event.target.value });
    };

    const changeLastNameHandler = (event) => {
        setState({ ...state, lastName: event.target.value });
    };

    const changeEmailHandler = (event) => {
        setState({ ...state, emailId: event.target.value });
    };

    return (
        <div>
            <br></br>
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        <h3 className="text-center">Add Employee</h3>
                        <div className="card-body">
                            <form>
                                <div className="form-group">
                                    <label> First Name: </label>
                                    <input placeholder="First Name" name="firstName" className="form-control"
                                        value={state.firstName} onChange={changeFirstNameHandler} />
                                </div>
                                <div className="form-group">
                                    <label> Last Name: </label>
                                    <input placeholder="Last Name" name="lastName" className="form-control"
                                        value={state.lastName} onChange={changeLastNameHandler} />
                                </div>
                                <div className="form-group">
                                    <label> Email Id: </label>
                                    <input placeholder="Email Address" name="emailId" className="form-control"
                                        value={state.emailId} onChange={changeEmailHandler} />
                                </div>

                                <button className="btn btn-success" onClick={saveEmployee}>Save</button>
                                <button className="btn btn-danger" onClick={cancel} style={{ marginLeft: "10px" }}>Cancel</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CreateEmployeeComponent;
