import React, { useState } from 'react';
import './LoginComponent.css'; 
import './ViewEmployeeComponent.css'; 
import { useNavigate } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';

const LoginComponent = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async () => {
        // Assuming your API returns user details based on the provided username
        const userDetails = await EmployeeService.getUserDetails(username);

        if (userDetails) {
            // Check if the entered password matches the user's password in userDetails
            if (password === userDetails.password) {
                // Successful login
                console.log('Login successful');
                navigate('/employees'); // Redirect to the employees page or any desired route
            } else {
                // Incorrect password
                console.log('Incorrect password');
            }
        } else {
            // User not found
            console.log('User not found');
        }
    };    

    return (
        <div className="login-container">
            <div className="view-employee-container"> 
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Login</h3>
                <div className="card-body">
                    <div className="row">
                       <label>Username:</label>
                       <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
                    </div>
                    <div className="row">
                       <label>Password:</label>
                       <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                    </div>
                    <div className="row">
                    <button type="button" onClick={handleLogin}>Login</button>
                    </div>
                </div>
            </div>
        </div>
        </div>

    );
};

export default LoginComponent;
