// CourseForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import CourseService from '../../services/CourseService';

const CourseForm = ({ onSubmit, initialCourse = {} }) => {
  const [course, setCourse] = useState(initialCourse);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCourse((prevCourse) => ({ ...prevCourse, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (initialCourse.id) {
        // If it's an existing course, update it
        await CourseService.updateCourse(initialCourse.id, course);
      } else {
        // If it's a new course, add it
        await CourseService.addCourse(course);
      }

      // Navigate to the courses page
      navigate('/courses');
    } catch (error) {
      console.error('Error submitting course:', error);
    }
  };

  return (
    <div>
      <h2>{initialCourse.id ? 'Edit Course' : 'Add Course'}</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Course Name:
          <input
            type="text"
            name="courseName"
            value={course.courseName || ''}
            onChange={handleChange}
          />
        </label>
        <button type="submit">{initialCourse.id ? 'Update' : 'Add'}</button>
      </form>
    </div>
  );
};

export default CourseForm;
