import React, { useState, useEffect } from 'react';
import DepartmentService from '../../services/DepartmentService';

const DepartmentForm = ({ onSave, department }) => {
  const [name, setName] = useState(department ? department.name : '');

  useEffect(() => {
    setName(department ? department.name : '');
  }, [department]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newDepartment = { name };

    if (department) {
      await DepartmentService.updateDepartment(department.id, newDepartment);
    } else {
      await DepartmentService.createDepartment(newDepartment);
    }

    onSave();
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{department ? 'Edit Department' : 'Add Department'}</h2>
      <label>Name:</label>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
      />
      <button type="submit">{department ? 'Update' : 'Create'}</button>
    </form>
  );
};

export default DepartmentForm;
