import React, { useState } from 'react';
import './LoginComponent.css'; 
import './ViewEmployeeComponent.css'; 
import { useNavigate } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';

const LoginComponent = () => {
    const navigate = useNavigate();
    const [state, setState] = useState({
        username: '',
        password: ''
    });

    const handleLogin = async () => {
        try {
            const user = {
                username: state.username,
                password: state.password
              };
              const userStatus=await EmployeeService.getUserDetails(user);
              if(userStatus.data){
                navigate('/employees');
              }else{
                navigate('/login');
              }
            } catch (error) {
            console.error('Error in logging: ', error);
            }
    };
    const changeUserNameHandler = (event) => {
        setState({ ...state, username: event.target.value });
    };

    const changePasswordHandler = (event) => {
        setState({ ...state, password: event.target.value });
    };

    return (
        <div className="login-container">
            <div className="view-employee-container"> 
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Login</h3>
                <div className="card-body">
                    <div className="row">
                       <label>Username:</label>
                       <input type="text" name="username" value={state.username} onChange={changeUserNameHandler} />
                    </div>
                    <div className="row">
                       <label>Password:</label>
                       <input type="password" name="password" value={state.password} onChange={changePasswordHandler}  />
                    </div>
                    <div className="row">
                    <button type="button" onClick={handleLogin}>Login</button>
                    </div>
                </div>
            </div>
        </div>
        </div>

    );
};

export default LoginComponent;
