import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';

const UpdateEmployeeComponent = () => {
    const [state, setState] = useState({
        firstName: '',
        lastName: '',
        emailId: ''
    });
    const [employees, setEmployees] = useState([]);
    useEffect(() => {
        EmployeeService.getEmployeeById().then((res) => {
            setEmployees(res.data);
        });
    }, []);

    const navigate = useNavigate();

    const updateEmployee = (e) => {
        e.preventDefault();
        let employee = { firstName: state.firstName, lastName: state.lastName, emailId: state.emailId };
        console.log('employee => ' + JSON.stringify(employee));
        EmployeeService.createEmployee(employee).then(res =>{
            navigate('/employees');
        }); 
    };

    const cancel = () => {
        // Navigate back to the employees page
        navigate('/employees');
    };

    const changeFirstNameHandler = (event) => {
        setState({ ...state, firstName: event.target.value });
    };

    const changeLastNameHandler = (event) => {
        setState({ ...state, lastName: event.target.value });
    };

    const changeEmailHandler = (event) => {
        setState({ ...state, emailId: event.target.value });
    };

    return (
        <div>
            <br></br>
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        <h3 className="text-center">Update Employee</h3>
                        <div className="card-body">
                            <form>
                                <div className="form-group">
                                    <label> First Name: </label>
                                    <input placeholder="First Name" name="firstName" className="form-control"
                                        value={state.firstName} onChange={changeFirstNameHandler} />
                                </div>
                                <div className="form-group">
                                    <label> Last Name: </label>
                                    <input placeholder="Last Name" name="lastName" className="form-control"
                                        value={state.lastName} onChange={changeLastNameHandler} />
                                </div>
                                <div className="form-group">
                                    <label> Email Id: </label>
                                    <input placeholder="Email Address" name="emailId" className="form-control"
                                        value={state.emailId} onChange={changeEmailHandler} />
                                </div>

                                <button className="btn btn-success" onClick={updateEmployee}>Save</button>
                                <button className="btn btn-danger" onClick={cancel} style={{ marginLeft: "10px" }}>Cancel</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UpdateEmployeeComponent;
