import React, { Component } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CreateEmployeeComponent = () => {
    const navigate = useNavigate();

    const [employee, setEmployee] = React.useState({
        firstName: '',
        lastName: '',
        emailId: ''
    });

    const changeHandler = (event) => {
        setEmployee({
            ...employee,
            [event.target.name]: event.target.value
        });
    };

    const saveEmployee = (e) => {
        e.preventDefault();

        // Make an HTTP POST request to your backend
        axios.post('http://your-backend-api-url/employees', employee)
            .then(response => {
                console.log('Employee created successfully:', response.data);
                // Redirect to the employees list page or perform other actions
                navigate('/employees');
            })
            .catch(error => {
                console.error('Error creating employee:', error);
            });
    };

    return (
        <div>
            <br />
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        <h3 className="text-center">Add Employee</h3>
                        <div className="card-body">
                            <form>
                                {/* ... your other form inputs */}
                                <button className="btn btn-success" onClick={saveEmployee}>Save</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CreateEmployeeComponent;
