import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import EmployeeService from '../services/EmployeeService';

const RegisterComponent = () => {
  const navigate = useNavigate();
  const [state, setState] = useState({
    email: '',
    password: '',
    firstName: '',
    lastName: ''
});
  const [error, setError] = useState(false);

  const changeFirstNameHandler = (event) => {
    setUser({ ...state, firstName: event.target.value });
};

const changeLastNameHandler = (event) => {
  setUser({ ...state, lastName: event.target.value });
};

const changeEmailHandler = (event) => {
  setUser({ ...state, email: event.target.value });
};
const changePasswordHandler = (event) => {
  setUser({ ...state, password: event.target.value });
};

  const handleSignUp = async (e) => {
    e.preventDefault();

    // Add your registration logic here
    // You can use the 'fetch' API or any other HTTP library for making requests

    // Example using fetch:
    try {
      const response = await EmployeeService.createUser(user);

      if (response.ok) {
        // Handle successful registration
        console.log('Registration successful');
        navigate(`/login/`);
      } else {
        // Handle registration failure
        setError(true);
      }
    } catch (error) {
      console.error('Error during registration:', error);
      setError(true);
    }
  };
  const loginAccount = () => {
    navigate(`/login/`);
  };
  return (
    <div>
      <h1>Sign Up</h1>
      {error && <p className="error">Registration failed</p>}
      <form>
                                <div className="form-group">
                                    <label>First Name:</label>
                                    <input
                                        type="text"
                                        name="firstName"
                                        className="form-control"
                                        value={state.firstName}
                                        onChange={changeFirstNameHandler}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Last Name:</label>
                                    <input
                                        type="text"
                                        name="lastName"
                                        className="form-control"
                                        value={state.lastName}
                                        onChange={changeLastNameHandler}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Email Id:</label>
                                    <input
                                        type="email"
                                        name="emailId"
                                        className="form-control"
                                        value={state.emailId}
                                        onChange={changeEmailHandler}
                                    />
                                </div>

                                <button className="btn btn-success" onClick={handleSignUp}>SignUP</button>
                                 </form>
      <p>Already have an account?</p> <br />
      <button onClick={() => loginAccount()}>Login</button>
    </div>
  );
};

export default RegisterComponent;
