import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import DepartmentService from '../../services/DepartmentService';

const ListDepartmentComponent = () => {
  const [departments, setDepartments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    DepartmentService.getAllDepartments()
      .then((res) => {
        console.log("Departments:", res); // Log the response to check the data
        setDepartments(res.data || []); // Access the correct property in the response
      })
      .catch((error) => {
        console.error('Error fetching departments:', error);
        setDepartments([]); // Set departments to an empty array in case of an error
      });
  }, []);  

  const addDepartment = () => {
    navigate('/add-department/_add');
  };
  const viewEmployees = () => {
    navigate('/employees');
  };

  const editDepartment = (id) => {
    navigate(`/add-department/${id}`);
  };

  const deleteDepartmentItem = async (id) => {
    try {
      const response = await DepartmentService.getDepartmentById(id);
      const department = response.data;

      if (department.employees && department.employees.length > 0) {
        alert('Cannot delete department with employees. Please remove employees first.');
      } else {
        await DepartmentService.deleteDepartment(id);
        setDepartments(departments.filter((department) => department.id !== id));
      }
    } catch (error) {
      console.error('Error deleting department:', error);
    }
  };


  return (
    <div>
      <h2 className="text-center">Departments List</h2>
      <div className="row">
      <button className="btn btn-info"style={{ marginLeft: '10px' }}
        onClick={addDepartment} >Add Department</button>
        <button className="btn btn-info"style={{ marginLeft: '10px' }}
        onClick={viewEmployees} >View Employees</button>
      </div>
      <br />
      <div className="row">
        <table className="table table-striped table-bordered">
          <thead>
            <tr>
              <th>Department Name</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {departments.map((department) => (
              <tr key={department.id}>
                <td>{department.name}</td>
                <td>
                  <button onClick={() => editDepartment(department.id)} className="btn btn-info">
                    Update
                  </button>
                  <button
                    style={{ marginLeft: '10px' }}
                    onClick={() => deleteDepartmentItem(department.id)}
                    className="btn btn-danger"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListDepartmentComponent;
