import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  getAllDepartments,
  deleteDepartment,
} from '../../services/DepartmentService';

const ListDepartmentComponent = () => {
  const [departments, setDepartments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getAllDepartments().then((res) => {
      setDepartments(res);
    });
  }, []);

  const addDepartment = () => {
    navigate('/add-department/_add');
  };

  const editDepartment = (id) => {
    navigate(`/add-department/${id}`);
  };

  const deleteDepartmentItem = (id) => {
    deleteDepartment(id).then(() => {
      setDepartments(departments.filter((department) => department.id !== id));
    });
  };

  return (
    <div>
      <h2 className="text-center">Departments List</h2>
      <div className="row">
        <button className="btn btn-primary" onClick={addDepartment}>
          Add Department
        </button>
      </div>
      <br />
      <div className="row">
        <table className="table table-striped table-bordered">
          <thead>
            <tr>
              <th>Department Name</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {departments.map((department) => (
              <tr key={department.id}>
                <td>{department.departmentName}</td>
                <td>
                  <button onClick={() => editDepartment(department.id)} className="btn btn-info">
                    Update
                  </button>
                  <button
                    style={{ marginLeft: '10px' }}
                    onClick={() => deleteDepartmentItem(department.id)}
                    className="btn btn-danger"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListDepartmentComponent;
