import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import CourseService from '../../services/CourseService';

const CourseList = () => {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    // Fetch courses
    CourseService.getAllCourses().then((res) => {
      setCourses(res.data || []);
    });
  }, []);

  return (
    <div>
      <h2 className="text-center">Course List</h2>
      <div className="row">
        <button className="btn btn-info" style={{ marginLeft: '10px' }} onClick={addCourse}>
          Add Course
        </button>
      </div>
      <br />
      <div className="row">
      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>Course Name</th>
            <th>Department</th>
            <th>Employee</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {courses.map((course) => (
            <tr key={course.id}>
              <td>{course.courseName}</td>
              <td>{course.department ? course.department.name : 'N/A'}</td>
              <td>{course.employee ? `${course.employee.firstName} ${course.employee.lastName}` : 'N/A'}</td>
              <td>
                <Link to={`/courses/${course.id}`}>View Details</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>
    </div>
  );
};

export default CourseList;
