import './LoginComponent.css';  // Import the CSS file

const LoginComponent = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async () => {
        // Perform API call to /api/v1/login with username and password
        // Handle the response accordingly (e.g., redirect on success)
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            <form>
                <label>Username:</label>
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
                <br />
                <label>Password:</label>
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                <br />
                <button type="button" onClick={handleLogin}>Login</button>
            </form>

            <div className="view-employee-container"> 
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Employee Details</h3>
                <div className="card-body">
                    <div className="row">
                        <label> First Name: </label>
                    </div>
                    <div className="row">
                        <label> Last Name: </label>

                    </div>
                    <div className="row">
                        <label> Email ID: </label>
                    </div>
                </div>
            </div>
        </div>
        </div>

    );
};

export default LoginComponent;
