import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import CourseService from '../../services/CourseService';
import DepartmentService from '../../services/DepartmentService';
import EmployeeService from '../../services/EmployeeService';


const CourseForm = () => {
  const { id } = useParams();
  const [course, setCourse] = useState({
    id: '',
    courseName: '',
    departmentId: '',
    employeeId: '',
  });

  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {

    // Fetch departments
    DepartmentService.getAllDepartments().then((res) => {
      setDepartments(res.data || []);
    });

    // Fetch employees (initially for the first department)
    if (departments.length > 0) {
      fetchEmployeesByDepartment(departments[0].id);
    }
  }, [departments]); // Empty dependency array for initial fetch only

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCourse({ ...course, [name]: value });
  };

  const handleDepartmentChange = (e) => {
    const departmentId = e.target.value;
    setCourse({ ...course, departmentId });
    fetchEmployeesByDepartment(departmentId);
  };

  const fetchEmployeesByDepartment = (departmentId) => {
    EmployeeService.getEmployeesByDepartment(departmentId).then((res) => {
      setEmployees(res.data || []);
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
        const course1 = {
            courseName: course.courseName,
            departmentId: course.departmentId,
            employeeId: course.employeeId,
        };

        if (id !== '_add') {
          await CourseService.updateCourse(course1, id);
        } else {
          await CourseService.createCourse(course1);
        }
        navigate('/courses');
      } catch (error) {
        console.error('Error saving/updating course: ', error);
      }
  };

  return (
    <div>
      <h3 className="text-center">{id ? 'Update Course' : 'Add Course'}</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Course Name:</label>
          <input
            type="text"
            name="courseName"
            value={course.courseName}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Department:</label>
          <select name="departmentId" value={course.departmentId} onChange={handleDepartmentChange} required>
            {departments.map((department) => (
              <option key={department.id} value={department.id}>
                {department.name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Employee:</label>
          <select name="employeeId" value={course.employeeId} onChange={handleInputChange} required>
            {employees.map((employee) => (
              <option key={employee.id} value={employee.id}>
                {employee.firstName} {employee.lastName}
              </option>
            ))}
          </select>
        </div>
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default CourseForm;
